    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}CATEGORIES
{% endblock %}

{% block body %}

    <div class=\"container\">

        {% for message in app.flashes('notice') %}
            <div class=\"alert alert-dismissible alert-success\">
                {{ message }}
            </div>
            <br><br>
        {% endfor %}
        <div class=\"row\">
            <div class=\"col s12 m12\">


                <h1>CATEGORY LIST
                </h2><br>

                <table class=\"table table-hover \">
                    <thead>
                        <tr class=\"table-info \">
                            <th scope=\"col \">Titles</th>

                            <th scope=\"col\">action</th>

                        </tr>
                    </thead>
                    <tbody>


                        {% for category in categories %}

                            <tr class=\"table-light\">

                                <td>{{category.name}}</td>

                                <td>
                                    <a href=\"{{path('category_show',{'name':category.name})}}\" type=\"button\" class=\"btn btn-dark\">
                                        <i class=\"fas fa-info\" ></i> Detail
                                    </a>
                                    <a href=\"{{path('category_edit',{'id':category.id})}}\" type=\"button\" class=\"btn btn-info\">
                                        <i class=\"fas fa-edit\" ></i> Edit
                                    </a>
                                    <a href=\"{{path('category_delete',{'id':category.id})}}\" type=\"button\" class=\"btn btn-danger\">
                                        <i class=\"fa fa-trash-alt\" > </i> Delete
                                    </a>
                                </td>

                            </tr>

                        {% endfor %}
                    </tbody>
                </table>
            </div>
        </div>
    </div>

{% endblock %}
", "category/index.html.twig", "/home/gilles/DEV/snowtrick/P6/templates/category/index.html.twig");
